import react, { Component } from "react";
import { Link } from "react-router-dom";
import "../components/Todo";

export default class Edit extends Component {
  state = {
    todo: "",
  };
  componentDidMount = () => {
    this.setState({
      todo: "",
    });
  };
  render() {
    const { todo } = this.props;
    console.log(todo);
    return (
      <div>
        <input type="text" value={todo}></input>
      </div>
    );
  }
}
