  state = {
    todo: [],
  };
  componentDidMount = () => {};

  render() {
    const { todoList } = this.state;
    const { todo } = this.props;
    console.log(todo);
    console.log(todoList);
    return (
      <div>
        <input type="text" value={todoList}></input>
      </div>
    );
  }
}
