import react, { Component } from "react";
import { Link } from "react-router-dom";

export default class Todo extends Component {
  state = {
    checked: false,
  };

  handleChecked = () => {
    const { checked } = this.state;
    if (checked === true) {
      this.setState({
        checked: true,
      });
    } else {
      this.setState({
        checked: false,
      });
    }
  };
  render() {
    const { id, Todo, del } = this.props;
    const { checked } = this.state;
    return <li id={id}></li>;
  }
}
