import { Component } from "react";
import { Link } from "react-router-dom";
import "./App";

export default class Edit extends Component {
  state = {
    todoList: [],
  };
  componentDidMount = () => {
    const { id } = this.props.match.params;
    const update = this.props.defaultValue;
    const data = localStorage.getItem("todoList")
      ? JSON.parse(localStorage.getItem("todoList"))
      : [];
    console.log('data',data);
    if (data.length > 0) {
      this.setState(
        {
          todoList: data[id],
        },
        () => {
          console.log(this.state);
        }
      );
    }
    
  };

  handleEditTodo = () => {
    const  updatevalue  = this.state;
    const { id } = this.props.match.params;
    if( [this.state[id]] === id) {
      this.setState({
        [this.state[id]] : updatevalue,
      });
    }
    
    console.log('hi',id,this.state,updatevalue);
  }

  render() {
    
    const { id } = this.props.match.params;
    const { todoList } = this.state;
    console.log(id);
    return (
      <div>
        <input
          className="edit"
          type="text"
          defaultValue={todoList[id]}
          onChange={(e) => this.setState({ updatevalue: e.target.value })}
        ></input>
        <button onClick={this.handleEditTodo}>
          <Link to="/todo">編輯完成</Link>
        </button>
      </div>
    );
  }
}
