import react, { Component } from "react";

export default class Create extends Component {
  state = {
    todo: "",
  };
  componentDidMount = () => {
    this.setState({
      todo: "",
    });
  };
  handleAddTodo = () => {
    const { todo } = this.state;
    let data = localStorage.getItem("todoList")
      ? JSON.parse(localStorage.getItem("todoList"))
      : [];
  };
}
