import { Component } from "react";
import { Link } from "react-router-dom";
import "./App";

export default class Edit extends Component {
  state = {
    todoList: [],
  };
  componentDidMount = () => {
    const { id } = this.props.match.params;
    const data = localStorage.getItem("todoList")
      ? JSON.parse(localStorage.getItem("todoList"))
      : [];
    console.log('data',data);
  };

  handleEditTodo = () => {
    const updatevalue  = this.state;
    const { id } = this.props.match.params;
    console.log('hi',updatevalue);
    const { todo } = this.state;
    let updatearr = localStorage.getItem("todoList")
      ? JSON.parse(localStorage.getItem("todoList"))
      : [];
      updatearr.push(todo);
      localStorage.setItem("todoList", JSON.stringify(updatearr));
      if(updatearr[id] === id ){
        this.setState({
          updatearr: updatevalue,
        });
      }
      this.props.history.push("/");
  }

  render() {
    const { id } = this.props.match.params;
    const { todoList } = this.state;
    return (
      <div>
        <input
          className="edit"
          type="text"
          defaultValue={todoList[id]}
          onChange={(e) => this.setState({ todoList: e.target.value })}
        ></input>
        <button onClick={this.handleEditTodo}>
          <Link to="/todo">編輯完成</Link>
        </button>
      </div>
    );
  }
}
